import textFragment from './fragment.glsl'
import textVertex from './vertex.glsl';

import * as THREE from 'three';

// import TRYGalien from './TRYGalien.woff2';

export default class Sphere extends React.PureComponent {
  constructor() {
    super();
    this.mouse = {
      x: 0,
      y: 0,
    }
  }
  componentDidMount() {
    this.width = window.innerWidth;
    this.height = window.innerHeight;

    this.renderer = new THREE.WebGLRenderer();

    this.renderer.setClearColor('hsl(100, 10%, 10%)', 1);


    this.camera = new THREE.PerspectiveCamera( 75, this.width / this.height, 0.1, 1000 );
    this.camera.position.set(2, -1.5, -1);

    this.camera.lookAt(new THREE.Vector3());
    // this.controls = new THREE.OrbitControls(this.camera, context.canvas);

    this.scene = new THREE.Scene();
    this.renderer.setSize(this.width, this.height);

    window.addEventListener('mousemove', this.handleMouseMove);
    this.animate();
  }

  handleMouseMove = (e) => {
    this.mouse.x = ( e.clientX / window.innerWidth ) * 2 - 1;
	  this.mouse.y = - ( e.clientY / window.innerHeight ) * 2 + 1;
  }

  animate = () => {
    requestAnimationFrame( this.animate );
  }

  render() {
    return (
      <div ref={(node) => { this.canvas = node}} />
    );
  }
}
