import textFragment from './fragment.glsl'
import textVertex from './vertex.glsl';
import PixiImage from './PixiImage';

// import TRYGalien from './TRYGalien.woff2';

export default class SphereSlider extends React.PureComponent {
  componentDidMount() {
    this.mouse = {
      x: 0,
      y: 0,
    }
    this.PIXI = require('pixi.js');
    this.width = window.innerWidth;
    this.height = window.innerHeight;
    this.app = new this.PIXI.Application({
      width: this.width,
      height: this.height,
      transparent: true,
      autoResize: true,
      resizeTo: window,
      resolution: 1,
      // forceFXAA: true,
      // antialias: true,
    });

    this.image = new PixiImage(
      img.url,
      {
        x: this.halfW,
        y: this.halfH,
        width: this.midSpriteSize.width * window.pixiScale,
        height: this.midSpriteSize.height * window.pixiScale,
        anchorX: 0.5,
        anchorY: 0.5,
      }
    )

    this.canvas.appendChild(this.app.view);

    window.addEventListener('mousemove', this.handleMouseMove);
    window.addEventListener('keydown', this.handleKeyDown);
    this.animate();
  }

  handleKeyDown = (e) => {
    if (e.key === 'ArrowUp') {
      console.log('ArrowUp');      
    }
    if (e.key === 'ArrowDown') {
      console.log('ArrowDown');
    }

  }

  handleMouseMove = (e) => {
    this.mouse.x = ( e.clientX / window.innerWidth ) * 2 - 1;
	  this.mouse.y = - ( e.clientY / window.innerHeight ) * 2 + 1;
  }

  animate = () => {
    requestAnimationFrame( this.animate );
  }

  render() {
    return (
      <div ref={(node) => { this.canvas = node}} />
    );
  }
}
