import textFragment from './fragment.glsl'
import textVertex from './vertex.glsl';

// import TRYGalien from './TRYGalien.woff2';

export default class SphereSlider extends React.PureComponent {
  componentDidMount() {
    this.mouse = {
      x: 0,
      y: 0,
    }
    this.PIXI = require('pixi.js');
    this.width = window.innerWidth;
    this.height = window.innerHeight;
    this.app = new this.PIXI.Application({
      width: this.width,
      height: this.height,
      transparent: true,
      autoResize: true,
      resizeTo: window,
      // forceFXAA: true,
      antialias: true,
    });

    this.canvas.appendChild(this.app.view);

    const basicText = new this.PIXI.Text('Basic text in pixi');
    basicText.x = this.width / 2;
    basicText.y = this.height / 2;

    const style = new this.PIXI.TextStyle({
        fontFamily: 'Arial',
        fontSize: 236,
        // fontStyle: 'italic',
        // fontWeight: 'bold',
        fill: ['#ffffff'], // gradient
        // stroke: '#4a1850',
        // strokeThickness: 5,
        // dropShadow: true,
        // dropShadowColor: '#000000',
        // dropShadowBlur: 4,
        // dropShadowAngle: Math.PI / 6,
        // dropShadowDistance: 6,
        wordWrap: true,
        wordWrapWidth: 440,
    });

    const richText = new this.PIXI.Text('ODDA', style);
    richText.x = this.width / 2;
    richText.y = this.height / 2;

    richText.anchor.set(0.5);

    this.filter = new this.PIXI.Filter(null, textFragment, {
      uTime: 0.0,
      uMouse: this.mouse,
    });

    richText.filters = [this.filter];

    this.app.stage.addChild(richText);

    window.addEventListener('mousemove', this.handleMouseMove);
    this.animate();
  }

  handleMouseMove = (e) => {
    mouse.x = ( e.clientX / window.innerWidth ) * 2 - 1;
	  mouse.y = - ( e.clientY / window.innerHeight ) * 2 + 1;
  }

  animate = () => {
    this.filter.uniforms.uTime++;
    this.filter.uniforms.uMouse = this.mouse;
    requestAnimationFrame( this.animate );
  }

  render() {
    return (
      <div ref={(node) => { this.canvas = node}} />
    );
  }
}
