import textFragment from './fragment.glsl'
import textVertex from './vertex.glsl';

// import TRYGalien from './TRYGalien.woff2';

export default class SphereSlider extends React.PureComponent {
  componentDidMount() {
    this.PIXI = require('pixi.js');
    const app = new this.PIXI.Application({ backgroundColor: 0x1099bb });
    this.canvas.appendChild(app.view);

    const basicText = new this.PIXI.Text('Basic text in pixi');
    basicText.x = 50;
    basicText.y = 100;

    app.stage.addChild(basicText);

    const style = new this.PIXI.TextStyle({
        fontFamily: 'Arial',
        fontSize: 36,
        fontStyle: 'italic',
        fontWeight: 'bold',
        fill: ['#ffffff', '#00ff99'], // gradient
        stroke: '#4a1850',
        strokeThickness: 5,
        dropShadow: true,
        dropShadowColor: '#000000',
        dropShadowBlur: 4,
        dropShadowAngle: Math.PI / 6,
        dropShadowDistance: 6,
        wordWrap: true,
        wordWrapWidth: 440,
    });

    const richText = new this.PIXI.Text('Rich text with a lot of options and across multiple lines', style);
    richText.x = 50;
    richText.y = 250;

  }

  animate = () => {
    this.material.needsUpdate = true;
    requestAnimationFrame( this.animate );
    // this.cube.rotation.x += 0.01;
		// this.cube.rotation.y += 0.01;
    this.renderer.render( this.scene, this.camera );
  }

  render() {
    return (
      <div ref={(node) => { this.canvas = node}} />
    );
  }
}
